import React from "react";
import classes from "./BoardTitle.module.scss";
import IconBoard from "../Icons/IconBoard";
import { useState } from "react";
const BoardTitle = ({ title }) => {
  const [boardIsSelected, setBoardIsSelected] = useState(false);
  let iconBoardColor = boardIsSelected ? "#f4f7fd" :"#828FA3";
  const className = boardIsSelected
    ? `${classes.board} ${classes.board_selected}`
    : classes.board;
  function selectBoard() {
    setBoardIsSelected(true);
  }
  function changeIconColor(){
    iconBoardColor="#828FA3"
  }
  return (
    <li className={className} onClick={selectBoard} onmouse>
      <span className={classes.boardItem}>
        <IconBoard fill={iconBoardColor} />
        <span>{title}</span>
      </span>
    </li>
  );
};

export default BoardTitle;
