  const [newTaskCreated, setNewTaskCreated] = useState(false);
  const boardName = useRef();
  const boardInput = <input  ref={boardName} onChange={handleChange} />;
  const addBoard = <button onClick={handleClick}>Create</button>
  const board = newBoardCreated ? enteredBoardName : "";

  function handleChange(event) {
    setNewTaskCreated(false);
    setEnteredBoardName(event.target.value);
  }
